dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		dom: 'Bfrtip',
		"ajax": "../../../../examples/ajax/data/objects.txt",
		deferRender: true,
		"columns": [
			{ "data": "name" },
			{ "data": "position" },
			{ "data": "office" },
			{ "data": "extn" },
			{ "data": "start_date" },
			{ "data": "salary" }
		],
		select: {
			mode: 'row',
			style: 'os',
			idSrc: 'name'
		},
		buttons: [
			'selected', 'selectedSingle', 'selectAll', 'selectNone',
			'selectRows', 'selectColumns', 'selectCells'
		]
	} );

	 $('#example').on( 'select.dt', function ( e, type, indexes ) {
		console.log( 'select event listener', type, indexes );
	} );

	 $('#example').on( 'deselect.dt', function ( e, type, indexes ) {
		console.log( 'deselect event listener', type, indexes );
	} );
} );
